//import { AccountService } from './../account.service';
import { Component } from '@angular/core';
import { VehicleService } from '../vehicle.service';
import { AccountService } from '../account.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-administrar-ve',
  templateUrl: './administrar-ve.component.html',
  styleUrls: ['./administrar-ve.component.css']
})
export class AdministrarVeComponent {
  haveReserve : boolean = false
  page : number = 0
  crearVe : boolean = false
  confirmarEliminar : boolean = false
  confirmarModificar : boolean = false
  confirmarReserva : boolean = false
  confirmarCrear : boolean = false
  id? : string
  type? : string
  model? : string
  reserva? : any
  address? : string
  vTypeValue? : string
  msg? : string = "Introduzca la matricula del vehículo"
  filterType? : string = "Coche"
  filterDis? : string
  helmet? : boolean
  lstVehiculos? : any
  lstVehicles? : any
  vehiculo? : any
  tipo? : number
  estado? : string = "DISPONIBLE"
  battery? : number = 100
  deactivated? : boolean 

  constructor(private vehicleService : VehicleService, private AccountService : AccountService, private Router : Router) {
    this.reserveExists()
  }

  isUser(){
    if (this.AccountService.user.role == "MEMBER"){
      return true
    } else {
      return false
    }
  }

  isAdmin(){
    if (this.AccountService.user.role == "ADMIN"){
        return true
    }else{
      return false
    }
}

  menu(){
    this.Router.navigate(['/inicio']);
  }

  delete(){
    this.confirmarEliminar=true
    this.msg = "Confime la eliminación del vehículo"
  }
  confirmDelete(){
    this.confirmarEliminar=false
    if (this.id==null){
      this.msg = "Introduce una matrícula"
    } else {
      this.vehicleService.delete(this.id).subscribe({
        next: respuesta => {
          this.cancelSelect()
          this.msg = "Eliminación exitosa"
        },
        error: error => {
          if (error.status == 409){
            this.msg = "El vehículo no se puede eliminar porque está reservado"
          } else {
            this.msg = "El vehículo no existe"
          }
        }
      })
    }
  }
  cancelDelete(){
    this.confirmarEliminar=false
    this.msg = "Eliminación cancelada"
  }
  mod(){
    this.confirmarModificar=true
    this.msg = "Confime la modificación del vehículo"
  }

  confirmMod(){
    this.confirmarModificar=false

    if (this.id==null){
      this.msg = "Introduce una matrícula"
      return
    }

    let info : any = {
      model : this.model,
      location: this.address,
      battery: this.battery
    }

    switch(this.type){
      case "Coche":
        info.seats = Number(this.vTypeValue)
        info.type = 1
        break
      case "Moto":
        info.includesHelmet = Boolean(this.vTypeValue)
        info.type = 2
        break
      case "Patinete":
        info.color = this.vTypeValue
        info.type = 3
        break
      default:
        this.msg = "Introduce un tipo de vehículo"
        return
    }

    this.vehicleService.mod(info, this.id).subscribe({
      next: _ => {
        this.msg = "Modificación exitosa"
      },
      error: _ => {
        this.msg = "Error al modificar"
      }
    })

  }

  reserveExists(){
    this.vehicleService.getReserves().subscribe({
      next: (respuesta: any) => {
        this.haveReserve = respuesta.id != undefined
      },
      error: _ => {}
    })
  }

  cancelMod(){
    this.confirmarModificar=false
    this.msg = "Modificación cancelada"
  }

  select(){
    if (this.id == null || this.id == ""){
      this.msg = "Introduce una matrícula"
    }else{
      this.vehiculo = null
      this.getVehiculo(this.id).subscribe({
        next: respuesta => {
          this.vehiculo = respuesta
          if (this.vehiculo["deactivated"] == true){
            this.msg = "Este vehículo no existe"
            return
          }
          this.infoVehiculo(this.vehiculo)
          this.page = 1
          this.msg = ""
        },
        error: _ => {
          this.msg = "Este vehículo no existe"
        }
      })
    }
  }

  redirectToReservations(){
    this.Router.navigate(['/reservations']);
  }

  cancelSelect(){
    this.page=0
    this.msg = "Introduzca la matricula del vehículo"
    this.crearVe = false
  }

  reserv(){
    this.confirmarReserva=true
    this.msg ="Confime la reserva del vehículo"
  }

  confirmReserv(){
    this.confirmarReserva=false
    this.estado = "RESERVADO"
    console.log(this.id);
    if (this.id !== undefined) {
      this.vehicleService.postReserve(this.id)
        .subscribe({
          next: (respuesta: any) => {
            console.log(respuesta);
            sessionStorage.setItem("idReserva", respuesta.id)
            this.Router.navigate(['/reservations']);
            this.msg = "Se ha reservado el vehículo correctamente";
          },
          error: error => {
            this.msg = "Ha ocurrido un error al reservar el vehículo";
          }
        });
    } else {
      this.msg = "Introduzca una matrícula";
    }
  }

  cancelReserv(){
    this.confirmarReserva=false
    this.msg = "Modificación cancelada"
  }

  //Selecciona la opción
  createV(){
    if (this.id == null || this.id == "") {
      this.msg = "Introduce una matrícula"
      return
    }

    this.vehicleService.getVehiculos(this.id).subscribe({
      next: respuesta => {
        this.msg = "Este vehículo ya existe"
      },
      error: _ => {
        this.infoVehiculoNull()
        this.msg = ""
        this.crearVe = true
        this.page = 1
      }
    })
  }

  //Crear el vehículo una vez dados los parámetros
  create(){
    this.confirmarCrear=true
    this.msg = "Confime la creación del vehículo"
  }

  confirmCreate(){
    this.confirmarCrear=false

    let info : any = {
      licensePlate : this.id,
      model : this.model,
      location: this.address,
      battery: 100,
    }

    switch(this.type){
      case "Coche":
        info.seats = Number(this.vTypeValue)
        info.type = 1
        break
      case "Moto":
        info.includesHelmet = Boolean(this.vTypeValue)
        info.type = 2
        break
      case "Patinete":
        info.color = this.vTypeValue
        info.type = 3
        break
      default:
        this.msg = "Introduce un tipo de vehículo"
        return
    }

    this.vehicleService.añadirVehiculo(info).subscribe({
      next: _ => {
        this.msg = "Creación exitosa"
      },
      error: _ => {
        this.msg = "Error al crear"
      }
    })

  }

  cancelCreate(){
    this.confirmarCrear=false
    this.msg = "Creación cancelada"
  }

  list(){
    this.page = 2
    this.filterDis="DISPONIBLE"
    this.consulta()
  }

  search() {
    this.lstVehicles = this.lstVehiculos.filter((vehiculo: { [x: string]: number | string | boolean; }) => {
      if (this.filterType == "Coche" && vehiculo["type"] == 1 && vehiculo["status"] == this.filterDis && vehiculo["deactivated"] == false) {
        return true;
      } else if (this.filterType == "Moto" && vehiculo["type"] == 2 && vehiculo["status"] == this.filterDis  && vehiculo["deactivated"] == false) {
        return true;
      } else if (this.filterType == "Patinete" && vehiculo["type"] == 3 && vehiculo["status"] == this.filterDis && vehiculo["deactivated"] == false) {
        return true;
      }
      return false;
    });

  }


  selecionarVe(pos : string){
    this.id = pos
    this.page = 1
    this.getVehiculo(pos).subscribe({
      next: respuesta => {
        this.vehiculo = respuesta
        this.infoVehiculo(this.vehiculo)
        this.msg = ""
      },
      error: _ => {
        this.msg = "Este vehículo no existe"
      }
    })
  }

  getVehiculo (matricula: string) {
    return this.vehicleService.getVehiculos(matricula)

  }

  infoVehiculoNull(){
    this.model = ""
    this.address = ""
    this.estado = "DISPONIBLE"
    this.battery = 100
    this.vTypeValue = ""
    this.type = ""
    this.deactivated = false
  }

  infoVehiculo(vehiculo : any){
    this.id = vehiculo["licensePlate"]
    this.model = vehiculo["model"]
    this.address = vehiculo["location"]
    this.estado = vehiculo["status"]
    this.battery = vehiculo["battery"]
    if (vehiculo["type"] == 1){
      this.vTypeValue = vehiculo["seats"]
      this.type = "Coche"
    } else if (vehiculo["type"] == 2){
      this.vTypeValue = vehiculo["includesHelmet"]
      this.type = "Moto"
    } else if (vehiculo["type"] == 3){
      this.vTypeValue = vehiculo["color"]
      this.type = "Patinete"
    }
    this.deactivated = vehiculo["deactivated"]
  }

  consulta(){
    //Cuando se ejecute se buscan los vehículos teniendo en cuenta los filtros establecidos
    this.vehicleService.getListaVehiculos()
    .subscribe({
      next: respuesta => {
        this.lstVehiculos = respuesta
        this.search()
        //this.infoVehiculo(this.lstVehiculos)
      },
      error: _ => {
        this.msg = "Ha ocurrido un error al buscar los vehículos";
      }
    })
  }
}
<!DOCTYPE html>
<html lang="es">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Vehículos</title>
    <style>
        body {
            background-color: lightblue;
            margin: 0;
            height: 100vh;
            display: grid;
        }

        form {
            grid-row: 4;
            grid-column: 2/4;
            display: grid;
            grid-template-rows: 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr;
            grid-template-columns: 1fr 1fr;
        }

        menu {
            grid-row: 4;
            grid-column: 2/4;
            display: grid;
            grid-template-rows: 1fr 1fr;
            grid-template-columns: 1fr 1fr;
        }

        .bloque-interfaz {
            grid-row: 2/8;
            grid-column: 2/4;
            background-color: white;
            border: 5px solid darkblue;
            border-radius: 50px;
        }

        .bloque-interfaz-li {
            grid-row: 2/4;
            grid-column: 2/6;
            background-color: white;
            border: 5px solid darkblue;
            border-radius: 50px;
        }

        .button-text {
            display: flex;
            align-self: end;
            justify-content: end;
            font-size: 1em;
            font-weight: bold;
        }

        .item {
            display: flex;
            justify-content: center;
            align-items: center;
        }

        .id-text {
            grid-row: 3;
            grid-column: 2;
            display: flex;
            align-items: center;
            font-weight: bold;
            margin-left: auto;
            margin-right: 10px;
        }

        .id-imput {
            grid-row: 3;
            grid-column: 3;
            display: flex;
            align-items: center;
        }

        .text-block {
            grid-row: 4;
            grid-column: 2;
            display: grid;
            align-content: flex-start;
        }

        .imput-block {
            grid-row: 4;
            grid-column: 3;
            display: grid;
            align-content: flex-start;
        }

        .item-text {
            display: flex;
            align-items: center;
            margin-left: auto;
            margin-right: 10px;
        }

        .text-input {
            display: flex;
            justify-content: center;
            align-items: center;
            width: 200px;
            padding: 5px;
            border: 1px solid #aaa;
            margin-left: 10px;
            margin-right: 10px;
        }

        .numeric-input {
            display: flex;
            justify-content: center;
            align-items: center;
            width: 90px;
            padding: 5px;
            border: 1px solid #aaa;
            margin-left: 10px;
        }

        .date-input {
            display: flex;
            justify-content: center;
            align-items: center;
            width: 90px;
            padding: 5px;
            border: 1px solid #aaa;
            margin-left: 10px;
        }

        li button * {pointer-events: none;}
    </style>
</head>
<body *ngIf="page==0 || page==1" style="grid-template-rows: 1fr 3fr 2fr 10fr 1fr 2fr 1fr 1fr; grid-template-columns: 6fr 5fr 5fr 6fr;">
    <div class="bloque-interfaz"></div>
    <div class="item" style="grid-row: 2; grid-column: 2 / 4; font-size: 4em; font-weight: bold;">
        Vehículos
    </div>
    <div class="id-text">
        Matrícula
    </div>
    <div class="id-imput">
        <input type="text" name="id" class="text-input" [(ngModel)]="id" [disabled]="page == 1">
    </div>
    <form *ngIf="page == 1">
        <div class="item-text" style="grid-row: 1; grid-column: 1;">Tipo</div>
        <div class="item-text" style="grid-row: 2; grid-column: 1;">Modelo</div>
        <div class="item-text" style="grid-row: 3; grid-column: 1;">Dirección</div>
        <div class="item-text" style="grid-row: 4; grid-column: 1;">Estado</div>
        <div class="item-text" style="grid-row: 5; grid-column: 1;">Batería</div>
        <div *ngIf="type=='Coche'" class="item-text" style="grid-row: 6; grid-column: 1;"> Número de plazas </div>
        <div *ngIf="type=='Moto'" class="item-text" style="grid-row: 6; grid-column: 1;"> Casco </div>
        <div *ngIf="type=='Patinete'" class="item-text" style="grid-row: 6; grid-column: 1;"> Color </div>
        <select type="text" name= "type" style="grid-row: 1; grid-column: 2;" class="text-input" [(ngModel)]="type" [disabled]="!crearVe">
            <option value="Coche">Coche</option>
            <option value="Moto">Moto</option>
            <option value="Patinete">Patinete</option>
        </select>
        <input type="text" name= "model" style="grid-row: 2; grid-column: 2;" class="text-input" [(ngModel)]="model" [disabled]=isUser()>
        <input type="text" name= "address" style="grid-row: 3; grid-column: 2;" class="text-input" [(ngModel)]="address" [disabled]=isUser()>
        <select type="text" name= "estado" style="grid-row: 4; grid-column: 2;" class="text-input" [(ngModel)]="estado" disabled="">
            <option value="DISPONIBLE">Disponible</option>
            <option value="RESERVADO">Reservado</option>
            <option value="EN_USO">En uso</option>
            <option value="DE_BAJA">De baja</option>
            <option value="PENDIENTE_CARGA">Pendiente de carga</option>
        </select>
        <input type="text" name= "battery" style="grid-row: 5; grid-column: 2;" class="text-input" [(ngModel)]="battery">
        <input *ngIf="type=='Coche'" type="number" min="1" name= "vTypeValue" style="grid-row: 6; grid-column: 2;" class="numeric-input" [(ngModel)]="vTypeValue" [disabled]=isUser()>
        <select *ngIf="type=='Moto'" type="text" name= "vTypeValue" style="grid-row: 6; grid-column: 2;" class="text-input" [(ngModel)]="vTypeValue" [disabled]=isUser()>
            <option value="true">Tiene casco</option>
            <option value="false">No tiene casco</option>
        </select>
        <input *ngIf="type=='Patinete'" type="text" name= "vTypeValue" style="grid-row: 6; grid-column: 2;" class="text-input" [(ngModel)]="vTypeValue" [disabled]=isUser()>
    </form>
    <div class="item" style="color: red; grid-row: 5; grid-column: 2 / 4; font-size: 15px; font-weight: bold;"> {{ msg }} </div>
    <div *ngIf="page == 1 && !confirmarEliminar && !confirmarModificar && !crearVe && isAdmin()" class="item" style="grid-row: 6; grid-column: 2;">
        <button (click)="delete()">
            Eliminar
        </button>
    </div>
    <div *ngIf="page == 1 && !confirmarEliminar && !confirmarModificar && !crearVe  && isAdmin()" class="item" style="grid-row: 6; grid-column: 3;">
        <button (click)="mod()">
            Modificar
        </button>
    </div>
    <div *ngIf="page == 1 && !confirmarReserva && estado=='DISPONIBLE' && isUser()" class="item" style="grid-row: 6; grid-column: 2/4;">
        <p *ngIf="this.haveReserve">Ya tienes una <a (click)="redirectToReservations()" style="color: blue; text-decoration: underline; cursor: pointer;">reserva activa</a>, finalizala antes de reservar otro vehículo<p>
        <button *ngIf="!this.haveReserve" (click)="reserv()">
            Reservar
        </button>
    </div>
    <div *ngIf="page == 1 && !confirmarCrear && crearVe" class="item" style="grid-row: 6; grid-column: 2/4;">
        <button (click)="create()">
            Crear Vehículo
        </button>
    </div>
    <div *ngIf="page == 1 && !confirmarEliminar && !confirmarModificar && !confirmarCrear" class="item" style="grid-row: 7; grid-column: 2/4; margin-bottom: 10px;">
        <button (click)="cancelSelect()">
            Cambiar Vehículo
        </button>
    </div>
    <div *ngIf="confirmarEliminar" class="item" style="grid-row: 6; grid-column: 2 / 4;">
        <button (click)="cancelDelete()" style="grid-column: 2; width: 100px; height: 50px; font-weight: bold; border-radius: 10px;">
            Cancelar Eliminación
        </button>
        <button (click)="confirmDelete()" style="grid-column: 3; width: 100px; height: 50px; font-weight: bold; border-radius: 10px;">
            Confirmar Eliminación
        </button>
    </div>
    <div *ngIf="confirmarModificar" class="item" style="grid-row: 6; grid-column: 2 / 4;">
        <button (click)="cancelMod()" style="grid-column: 2; width: 100px; height: 50px; font-weight: bold; border-radius: 10px;">
            Cancelar Modificación
        </button>
        <button (click)="confirmMod()" style="grid-column: 3; width: 100px; height: 50px; font-weight: bold; border-radius: 10px;">
            Confirmar Modificación
        </button>
    </div>
    <div *ngIf="confirmarReserva" class="item" style="grid-row: 6; grid-column: 2 / 4;">
        <button (click)="cancelReserv()" style="grid-column: 2; width: 100px; height: 50px; font-weight: bold; border-radius: 10px;">
            Cancelar Reserva
        </button>
        <button (click)="confirmReserv()" style="grid-column: 3; width: 100px; height: 50px; font-weight: bold; border-radius: 10px;">
            Confirmar Reserva
        </button>
    </div>
    <div *ngIf="confirmarCrear" class="item" style="grid-row: 6; grid-column: 2 / 4;">
        <button (click)="cancelCreate()" style="grid-column: 2; width: 100px; height: 50px; font-weight: bold; border-radius: 10px;">
            Cancelar Creación
        </button>
        <button (click)="confirmCreate()" style="grid-column: 3; width: 100px; height: 50px; font-weight: bold; border-radius: 10px;">
            Confirmar Creación
        </button>
    </div>
    <menu *ngIf="page == 0 && isAdmin()" style="padding: 0;">
        <button (click)="createV()" style="grid-column: 1; grid-row: 1/span2; width: 200px; height: 100px; font-size: 2em; font-weight: bold; border-radius: 40px; align-self: center; margin-left: auto; margin-right: 10px; background-color: darkblue; color: white;">
            Crear vehículo
        </button>
        <button (click)="select()" style="grid-column: 2; grid-row: 1; width: 200px; height: 100px; font-size: 2em; font-weight: bold; border-radius: 40px; align-self: center; margin-left: 10px; margin-right: auto; background-color: darkblue; color: white;">
            Seleccionar vehículo
        </button>
        <button (click)="list()" style="grid-column: 2; grid-row: 2; width: 200px; height: 100px; font-size: 2em; font-weight: bold; border-radius: 40px; align-self: center; margin-left: 10px; margin-right: auto; background-color: darkblue; color: white;">
            Consultar lista
        </button>
    </menu>
    <menu *ngIf="page == 0 && isUser()" style="padding: 0;">
        <button (click)="select()" style="grid-column: 1; grid-row: 1/span2; width: 200px; height: 100px; font-size: 2em; font-weight: bold; border-radius: 40px; align-self: center; margin-left: auto; margin-right: 10px; background-color: darkblue; color: white;">
            Seleccionar vehículo
        </button>
        <button (click)="list()" style="grid-column: 2; grid-row: 1/span2; width: 200px; height: 100px; font-size: 2em; font-weight: bold; border-radius: 40px; align-self: center; margin-left: 10px; margin-right: auto; background-color: darkblue; color: white;">
            Consultar lista
        </button>
    </menu>
    <div *ngIf="page == 0" class="item" style="grid-row: 7; grid-column: 2 / 4;">
        <button (click)="menu()" style="grid-row: 7; grid-column: 2 / 4; margin-bottom: 10px; width: 250px; height: 50px; font-size: 2em; font-weight: bold; border-radius: 40px; background-color: darkblue; color: white;">
            Volver al menu
        </button>
    </div>
</body>
<body *ngIf="page==2" style="min-height: 100vh; height: 100%; grid-template-rows: 10px 100px 15fr 1fr; grid-template-columns: 6fr 1fr 5fr 5fr 1fr 6fr;">
    <div class="bloque-interfaz-li"></div>
    <div class="bloque-interfaz-li" style="grid-row: 2; background-color: blue; display: grid; grid-template-rows: 1fr 4fr 1fr 4fr 1fr; grid-template-columns: 1fr 4fr 10px 4fr 20px 4fr 1fr;">
        <div class="item-text" style="justify-content: end; font-weight: bold; grid-row: 2; grid-column: 2; color: white">
            Tipo:
        </div>
        <select type="item-text" name="filterType" style="justify-content: start; grid-row: 2; grid-column: 4;" class="text-input" [(ngModel)]="filterType">
            <option value="Coche">Coche</option>
            <option value="Moto">Moto</option>
            <option value="Patinete">Patinete</option>
        </select>
        <div class="item-text" style="justify-content: end; font-weight: bold; grid-row: 4; grid-column: 2; color: white">
            Disponible:
        </div>
        <select type="item-text" name="filterDis" style="justify-content: start; grid-row: 4; grid-column: 4;" class="text-input" [(ngModel)]="filterDis" [disabled]="isUser()">
            <option value="DISPONIBLE">Disponible</option>
            <option value="RESERVADO">Reservado</option>
            <option value="EN_USO">En uso</option>
            <option value="DE_BAJA">De baja</option>
            <option value="PENDIENTE_CARGA">Pendiente de carga</option>
        </select>
        <button (click)="search()" style="grid-row: 2; grid-column: 6; border-radius: 25px;">
            Buscar
        </button>
        <button (click)="cancelSelect()" style="grid-row: 4; grid-column: 6; border-radius: 25px;">
            Volver
        </button>
    </div>
    <div class="item" style="grid-row: 3; grid-column: 3/5; flex-direction: column;">
      <li *ngFor="let vehicle of lstVehicles" style="list-style: none;">
        <button (click)="selecionarVe(vehicle['licensePlate'])" style="width: 100%; height: 40px; margin: 10px; display: grid; grid-template-rows: 5px 1fr 10px 1fr 5px; grid-template-columns: 1fr 2fr 5px 4fr 10px 2fr 5px 2fr 1fr; background-color: aquamarine; border-radius: 20px;">
            <div class="button-text" style="grid-row: 2; grid-column: 2;">
                Matrícula:
            </div>
            <input type="text" style="height: 100%; width: 100%; grid-row: 2; grid-column: 4;" [(ngModel)]="vehicle['licensePlate']" [disabled]=true>
            <div class="button-text" style="grid-row: 2; grid-column: 6;">
                Disponible:
            </div>
            <input type="text" style="height: 100%; width: 100%; grid-row: 2; grid-column: 8;" [(ngModel)]="vehicle['status']" [disabled]=true>
        </button>
      </li>
      <p *ngIf="lstVehicles.length==0">No se han encontrado vehículos</p>
    </div>
</body>
</html>
