import { Component } from '@angular/core';
import { AccountService } from '../account.service';
import { Router } from '@angular/router';
import { ReservationsComponent } from '../reservations/reservations.component';
import { VehicleService } from '../vehicle.service';

@Component({
  selector: 'app-consultas-telefonicas',
  templateUrl: './consultas-telefonicas.component.html',
  styleUrls: ['./consultas-telefonicas.component.css']
})
export class ConsultasTelefonicasComponent {

  constructor(public accountService: AccountService, private router: Router, private vehicleService : VehicleService) {}
  
  page : number = 1
  mostrarLista : boolean = true
  confirmarFinalizacion : boolean = false
  confirmarCancelacion : boolean = false
  status?: string = 'ACTIVA'
  id: string = '' //Para que funcione bien el html no puede ser nulo
  vehicle?: string
  user?: string
  endDate?: Date
  startDate?: Date
  valuation?: number
  comment?: string
  msg?: string = "Elija una opción"
  filterStatus?: string = 'FINALIZADA'
  reserva? : any
  lstReserv? : any
  lstReserves? : any

  redirectToReservas() {
    this.router.navigate(['/reservations'])
  }

  redirectToVehiculos() {
    this.router.navigate(['/vehiculos']);
  }

  menu(){
    this.router.navigate(['/inicio']);
  }

  search(){
        //Cuando se ejecute se buscan los vehículos teniendo en cuenta los filtros establecidos
        this.lstReserv = this.lstReserves.filter((reserva: { [x: string]: string }) => {
          if (this.filterStatus == "ACTIVA" && reserva["state"] == "ACTIVA") {
            return true;
          } else if (this.filterStatus == "CANCELADA" && reserva["state"] == "CANCELADA") {
            return true;
          } else if (this.filterStatus == "FINALIZADA" && reserva["state"] == "FINALIZADA") {
            return true;
          }
          return false;
        });
  }

  cancelSelect(){
    this.page = 1
  }

  selecionarRe(pos : any){
    console.log(pos["id"])
    this.id = pos["id"]
    this.endDate = pos["end"]
    this.startDate = pos["start"]
    this.user = pos["userId"]
    this.status = pos["state"]
    this.vehicle = pos["vehicle"]["licensePlate"]
    this.valuation = pos["rating"]
    this.comment = pos["comment"]
    this.page = 0
  }

  listReservasTelefonicas(){
    this.page = 2
    this.consultaTelefonica()
  }

  consultaTelefonica(){
    //Cuando se ejecute se buscan los vehículos teniendo en cuenta los filtros establecidos
    console.log("HOLA")
    this.vehicleService.getLstReservesTelefonicas()
    .subscribe({
      next: respuesta => {
        console.log(respuesta)
        this.msg = ""
        this.lstReserves = respuesta
        this.search()
        //this.infoVehiculo(this.lstVehiculos)
      },
      error: error => {
        this.msg = "Ha ocurrido un error al mostrar la reserva especificada";
      }
    })
  }
}
<!DOCTYPE html>
<html lang="es">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Menú</title>
    <style>
        body {
            background-color: lightblue;
            margin: 0;
            height: 100vh;
            display: grid;
            grid-template-rows: 8fr 1fr 1fr 1fr 1fr 6fr 1fr 1fr 2fr 6fr;
        }
        form {
            grid-row: 3;
            grid-column: 2/4;
            display: grid;
            grid-template-rows: 1fr 1fr 1fr 1fr 1fr 1fr;
            grid-template-columns: 1fr 1fr;
        }
        
        .bloque-interfaz {
            grid-row: 2/9;
            grid-column: 2/5;
            background-color: white;
            border: 5px solid darkblue;
            border-radius: 50px;
        }
        .bloque-interfaz-li {
            grid-row: 2/4;
            grid-column: 2/6;
            background-color: white;
            border: 5px solid darkblue;
            border-radius: 50px;
        }
        .text {
            display: flex;
            align-self: end;
            justify-content: start;
            font-size: 2em;
            font-weight: bold;
        }
        .id-text {
            grid-row: 3;
            grid-column: 2;
            display: flex;
            align-items: center;
            font-weight: bold;
            margin-left: auto;
            margin-right: 10px;
        }
        .item {
            display: flex;
            justify-content: center;
            align-items: center;
        }
        .item-text {
            display: flex;
            align-items: center;
            margin-left: auto;
            margin-right: 10px;
        }
        .grid {
            height: 100vh;
            display: grid;
            grid-template-rows: 8fr 1fr 1fr 1fr 1fr 10fr 1fr 1fr 2fr 6fr;
            grid-template-columns: 10fr 1fr 40fr 1fr 10fr;
        }
        .home-btn {
            font-size: 2em;
            font-weight: bold;
            border-radius: 50px;
            background-color: darkblue;
            color: white;
            height: 50%;
            width: 100%;
        }
        .menu-btn {
            width: 30%;
        }
    </style>
</head>
<body *ngIf="page==1">
    <div class="grid">
        <div class="bloque-interfaz"></div>
        <div class="item" style="grid-row: 3; grid-column: 3; font-size: 4em; font-weight: bold; text-align: center;">
        </div>
        <div *ngIf="accountService.user.role=='TELEPHONEATTENTION'" class="item menu-btn" style="grid-row: 7; grid-column: 3; height: 50px; width: 100%;">
            <button (click)="menu()" class="home-btn" style="height: 100%; width: auto; background-color: blue;">
                Volver
            </button>
        </div>
        <div style="grid-row: 6; grid-column: 3; display: flex; justify-content: space-between;">
            <div *ngIf="accountService.user.role=='TELEPHONEATTENTION'" class="item menu-btn">
                <button (click)="redirectToVehiculos()" class="home-btn">
                    Vehiculos
                </button>
            </div>
            <div *ngIf="accountService.user.role=='TELEPHONEATTENTION'" class="item menu-btn">
                <button (click)="listReservasTelefonicas()" class="home-btn">
                    Reservas
                </button>
            </div>
        </div>
    </div>
</body>

<body *ngIf="page==0" style="grid-template-rows: 1fr 3fr 7fr 6fr 1fr 2fr 1fr 1fr; grid-template-columns: 6fr 5fr 5fr 6fr;">
    <div class="bloque-interfaz"></div>
    <div *ngIf="(page==0) || !(id==='')" class="item" style="grid-row: 2; grid-column: 2 / 4; font-size: 4em; font-weight: bold;">
        Reservas
    </div>
    <form *ngIf="page==0">
        <div class="item-text" style="grid-row: 1; grid-column: 1;">ID reserva</div>
        <div class="item-text" style="grid-row: 2; grid-column: 1;">Matrícula vehículo</div>
        <div class="item-text" style="grid-row: 3; grid-column: 1;">Estado</div>
        <div class="item-text" style="grid-row: 4; grid-column: 1;">Fecha de inicio</div>
        <div class="item-text" style="grid-row: 5; grid-column: 1;">Fecha de fin</div>
        <div *ngIf="(status=='FINALIZADA') || (confirmarFinalizacion)" class="item-text" style="grid-row: 6; grid-column: 1;">Valoración</div>
        <input type="text" name="id" style="grid-row: 1; grid-column: 2;" class="text-input" [(ngModel)]="id" [disabled]=true>
        <input type="text" name="vehicle" style="grid-row: 2; grid-column: 2;" class="text-input" [(ngModel)]="vehicle" [disabled]=true>
        <select type="text" name="status" style="grid-row: 3; grid-column: 2;" class="text-input" [(ngModel)]="status" [disabled]=true>
            <option value="ACTIVA">Activa</option>
            <option value="CANCELADA">Cancelada</option>
            <option value="FINALIZADA">Finalizada</option>
        </select>
        <input type="text" name="startDate" style="grid-row: 4; grid-column: 2;" class="date-input" [(ngModel)]="startDate" [disabled]=true>
        <input type="text" name="endDate" style="grid-row: 5; grid-column: 2;" class="date-input" [(ngModel)]="endDate" [disabled]=true>
        <input *ngIf="(status=='FINALIZADA') || (confirmarFinalizacion)" type="number" name="valuation" style="grid-row: 6; grid-column: 2;" min="0" max="10" class="numeric-input" [(ngModel)]="valuation" [disabled]=!confirmarFinalizacion>
    </form>
    <div *ngIf="(page==0) && !(id==='') && ((status=='FINALIZADA') || (confirmarFinalizacion))" style="grid-row: 4; grid-column: 2/4; display: grid; grid-template-rows: 1fr 5fr 1fr;  grid-template-columns: 1fr 6fr 1fr">
        <div class="item-text" style="grid-row: 1; grid-column: 2; margin-left: 10px; margin-right: auto;">Comentario:</div>
        <textarea type="text" name="comment" style="grid-row: 2; grid-column: 2; height: 100%; width: 100%;" class="text-input" [(ngModel)]="comment" [disabled]=!confirmarFinalizacion></textarea>
    </div>
    <div *ngIf="page == 0 && !confirmarCancelacion && !confirmarFinalizacion" class="item" style="grid-row: 7; grid-column: 2/4; margin-bottom: 10px;">
        <button (click)="cancelSelect()">
            Menu reservas
        </button>
    </div>
    <div *ngIf="page == 0" class="item" style="grid-row: 7; grid-column: 2 / 4;">
        <button (click)="menu()" style="grid-row: 7; grid-column: 2 / 4; margin-bottom: 10px; width: 250px; height: 50px; font-size: 2em; font-weight: bold; border-radius: 40px; background-color: darkblue; color: white;">
            Volver al menu
        </button>
    </div>
</body>



<body *ngIf="page==2" style="min-height: 100vh; height: 100%; grid-template-rows: 10px 100px 15fr 1fr; grid-template-columns: 6fr 1fr 5fr 5fr 1fr 6fr;">
    <div class="bloque-interfaz-li"></div>
    <div class="bloque-interfaz-li" style="grid-row: 2; background-color: blue; display: grid; grid-template-rows: 1fr 4fr 1fr 4fr 1fr; grid-template-columns: 1fr 4fr 10px 4fr 20px 4fr 1fr;">
        <div class="item-text" style="justify-content: end; font-weight: bold; grid-row: 2/span3; grid-column: 2; color: white">
            Estado:
        </div>
        <select type="item-text" name="filterStatus" style="height: 40%; align-self: center; grid-row: 2/span3; grid-column: 4;" class="text-input" [(ngModel)]="filterStatus">
            <option value="ACTIVA">Activa</option>
            <option value="CANCELADA">Cancelada</option>
            <option value="FINALIZADA">Finalizada</option>
        </select>
        <button (click)="search()" style="grid-row: 2; grid-column: 6; border-radius: 25px;">
            Buscar
        </button>
        <button (click)="cancelSelect()" style="grid-row: 4; grid-column: 6; border-radius: 25px;">
            Volver
        </button>
    </div>
    <div class="item" style="grid-row: 3; grid-column: 3/5; flex-direction: column; list-style: none;">
      <li *ngFor="let reserv of lstReserv" style="list-style: none;">
        <button (click)="selecionarRe(reserv)" style="width: 100%; height: 40px; margin: 10px; display: grid; grid-template-rows: 5px 1fr 10px 1fr 5px; grid-template-columns: 1fr 2fr 5px 3fr 10px 2fr 5px 2fr 7px 2fr 5px 2fr 1fr; background-color: aquamarine; border-radius: 20px;">
            <div class="button-text" style="grid-row: 2; grid-column: 2;">
                Matricula:
            </div>
            <input type="text" style="height: 100%; width: 100%; grid-row: 2; grid-column: 4;" [(ngModel)]="reserv['vehicle']['licensePlate']" [disabled]=true>
            <div class="button-text" style="grid-row: 2; grid-column: 6;">
                Inicio:
            </div>
            <input type="text" style="height: 100%; width: 100%; grid-row: 2; grid-column: 8;" [(ngModel)]="reserv['start']" [disabled]=true>
            <div class="button-text" style="grid-row: 2; grid-column: 10;">
                Fin:
            </div>
            <input type="text" style="height: 100%; width: 100%; grid-row: 2; grid-column: 12;" [(ngModel)]="reserv['end']" [disabled]=true>
        </button>
      </li>
      <p *ngIf="lstReserv.length==0">No se han encontrado reservas</p>
    </div>
</body>

</html>
