import { Component } from '@angular/core';
import { AccountService } from '../account.service';
import { Router } from '@angular/router';
import { ReservationsComponent } from '../reservations/reservations.component';
import { VehicleService } from '../vehicle.service';

@Component({
  selector: 'app-consultas-telefonicas',
  templateUrl: './consultas-telefonicas.component.html',
  styleUrls: ['./consultas-telefonicas.component.css']
})
export class ConsultasTelefonicasComponent {

  constructor(public accountService: AccountService, private router: Router, private reservations: ReservationsComponent, private vehicleService : VehicleService) {}

  redirectToReservas() {
    //this.router.navigate(['/reservations'])
  }

  redirectToVehiculos() {
    //this.router.navigate(['/vehiculos']);
  }

  menu(){
    //this.router.navigate(['/inicio']);
  }
}
<!DOCTYPE html>
<html lang="es">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Menú</title>
    <style>
        body {
            background-color: lightblue;
            margin: 0;
            height: 100vh;
            display: grid;
            grid-template-rows: 8fr 1fr 1fr 1fr 1fr 6fr 1fr 1fr 2fr 6fr;
        }
        .bloque-interfaz {
            grid-row: 2/9;
            grid-column: 2/5;
            background-color: white;
            border: 5px solid darkblue;
            border-radius: 50px;
        }
        .text {
            display: flex;
            align-self: end;
            justify-content: start;
            font-size: 2em;
            font-weight: bold;
        }
        .item {
            display: flex;
            justify-content: center;
            align-items: center;
        }
        .grid {
            height: 100vh;
            display: grid;
            grid-template-rows: 8fr 1fr 1fr 1fr 1fr 10fr 1fr 1fr 2fr 6fr;
            grid-template-columns: 10fr 1fr 40fr 1fr 10fr;
        }
        .home-btn {
            font-size: 2em;
            font-weight: bold;
            border-radius: 50px;
            background-color: darkblue;
            color: white;
            height: 50%;
            width: 100%;
        }
        .menu-btn {
            width: 30%;
        }
    </style>
</head>
<body>
    <div class="grid" *ngIf="accountService.user.dni!=null">
        <div class="bloque-interfaz"></div>
        <div class="item" style="grid-row: 3; grid-column: 3; font-size: 4em; font-weight: bold; text-align: center;">
            ¡Bienvenido {{ accountService.user.name }}!
        </div>
        <div style="grid-row: 6; grid-column: 3; display: flex; justify-content: space-between;">
            <div *ngIf="accountService.user.role=='MEMBER'" class="item menu-btn">
                <button (click)="redirectToReservas()" class="home-btn">
                    Reservas
                </button>
            </div>
            <div *ngIf="accountService.user.role=='MEMBER'" class="item menu-btn">
                <button (click)="redirectToVehiculos()" class="home-btn">
                    Consultas 
                </button>
            </div>
           
            <div *ngIf="accountService.user.role=='ADMIN'" class="item menu-btn">
                <button (click)="redirectToVehiculos()" class="home-btn">
                    Administrar Vehiculos
                </button>
            </div>
        </div>
    </div>
</body>
</html>
